                    fill: true, 
                }],
        }}
    />) : null
    );
    const lineChart = (
        dailyData.length
            ? (
            <Line
            data={{
                labels: dailyData.map(({ date }) => date),
                datasets: [{
                    data: dailyData.map(({ confirmed }) => confirmed),
                    label: 'Infected',
                    borderColor: 'red',
                    fill: true,

                }, {
                    data: dailyData.map(({ deaths }) => deaths),
                    label: 'Deaths',
                    borderColor: 'black',
                    fill: true, 
                }],
        }}
    />) : null
